////Applications
import axios from "axios";
import { useQuery } from "react-query";

import { fetchStudentsKeyByPage } from "../../../util/queryKeys";
const fetchStudentsByPage = async (_pageSize, _pageIndex) => {
  const res = await axios.get(
    `http://127.0.0.1:8000/students-paging/${_pageSize}/${_pageIndex}`
  );
  return res?.data;
};
///creat function
const useGetDataByPage = (pageSize, pageIndex) => {
  const query = useQuery(
    [fetchStudentsKeyByPage, pageSize, pageIndex],
    fetchStudentsByPage(pageSize, pageIndex)
  );

  const {
    isLoading: isLoadingStudent,
    isError: isErrorStudent,
    isSuccess: isSuccessStudent,
    refetch: refetchStudent,
    remove: removeStudent,
    data: dataStudent,
    error: errorStudent,
  } = query;
  return {
    dataStudent,
    isLoadingStudent,
    refetchStudent,
    removeStudent,
    dataStudent,
    errorStudent,
  };
};
export default useGetDataByPage;
