    }
  return (
    <div>
      <div className="main__title">
        <h1>Manage Data Student</h1>
      </div>

      <div className="main__Add"></div>
      <div className="main__body">
        <DataGrid
          dataSource={studentDataSource}
          remoteOperations={true}
          ref={gridRef}
          repaintChangesOnly={true}
          onCellClick={onCellClick}
          editingStart={editingStart}
          selectedRowKeys={selectedRowKeys}
          onInitNewRow={onInitRow}
          onSelectionChanged={selectionChanged}
        >
          <Editing
            mode="cell"
            useIcons={true}
            changes={changes}
            onChangesChange={onChangesChange}
            // editRowKey={editRowKey}
            // onEditRowKeyChange={onEditRowKeyChange}

            startEditAction
            selectTextOnEditStart={true}
          >
            <TextField label="Student"></TextField>
          </Editing>

          <Column
            dataField="nameStudent"
            dataType="string"
            showEditorAlways={false}
          />
          <Column
            dataField="phoneStudent"
            dataType="string"
            showEditorAlways={false}
          />
          <Column
            dataField="dateOfBirth"
            dataType="date"
            format="dd/MM/yyyy"
            showEditorAlways={false}
          />
          <Column
            dataField="createDate"
            dataType="date"
            visible={false}
            defaultSortOrder="asc"
          />

          <Column
            dataField="scoreStudent"
            dataType="number"
            showEditorAlways={false}
          />
          <Column dataField="" cellRender={renderButton}></Column>

          <Toolbar>
            <Item location="after">
              <Button
                variant="contained"
                icon="refresh"
                onClick={() => {
                  refetch();
                  toast("ðŸ¦„ You are refetched", {
                    position: "top-right",
                    autoClose: 5000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                  });
                }}
              >
                refetch
              </Button>
            </Item>
            {/* <Item location="after">
              <Button
                variant="contained"
                icon="refresh"
                onClick={() => gridRef.current?.instance?.saveEditData()}
                viable={false}
              >
                Save
              </Button>
            </Item> */}
            <Item>
              <Button
                variant="contained"
                onClick={() => gridRef.current?.instance?.addRow()}
              >
                add
              </Button>
            </Item>
          </Toolbar>
          <Paging defaultPageSize={12} />
          <Pager showPageSizeSelector={true} />
        </DataGrid>
      </div>

      {/* ///React Hook Form + Material Setup */}
    </div>
  );
};
//Create content customize Form

export default CellStudent;
