  const res = await axios.get(`http://127.0.0.1:8000/students`,{student});
  return res?.data;
};
const createStudent = async (newStudent) => {
  console.log("createStudent", newStudent);
  await (
    await axios.post("http://127.0.0.1:8000/students", newStudent)
  ).data;
};
///creat function
const useCreateData = (student) => {
    const  { isLoading, isError, isSuccess, refetch, remove, data, error } =
